import React, { useState } from "react";

function Model() {
  const [file, setFile] = useState(null);

  function handleFileChange(event: any) {
    // Update the state with the selected file
    setFile(event.target.files[0]);
  }
  const handleUpload = (event: any) => {
    if (event.state.selectedFile) {
        return (
          <div>
            <h2>File Details:</h2>
            <p>File Name: {event.state.selectedFile.name}</p>
            <p>File Type: {event.state.selectedFile.type}</p>
            <p>
              Last Modified:{' '}
              {this.state.selectedFile.lastModifiedDate.toDateString()}
            </p>
          </div>
        );
      } else {
        return (
          <div>
            <br />
            <h4>Choose before Pressing the Upload button</h4>
          </div>
        );
      }
  };
  return (
    <div className="container upload mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <h5>File Upload</h5>
          <input
            type="file"
            className="form-control mb-3 custom-file-input"
            onChange={handleFileChange}
          />
          <a className="btn " onClick={handleUpload}>
            Upload
          </a>
        </div>
      </div>
    </div>
  );
}

export default Model;
